C++<pre><code>#include &lt;iostream&gt;
using namespace std;

class Dst {
public:
    Dst() {
        cout &lt;&lt; "Dst::Dst()\n";
    }
    // 方法一：在目标类中定义【源类对象作参数的构造函数】
    Dst(const Src&amp; s) {
        cout &lt;&lt; "Dst::Dst(const Src&amp;) called\n";
    }
};

class Src {
public:
    Src() {
        cout &lt;&lt; "Src::Src()\n";
    }
    // 方法二：在源类中定义【目标类型转换运算符】
    operator Dst() const {
        cout &lt;&lt; "Src::operator Dst() called\n";
        return Dst();
    }
};

// 测试代码，注意两种方法不可同时使用
void func(Dst d) { }

int main() {
    Src s;
    Dst d1(s);      // 直接构造

    Dst d2 = s;     // 自动类型转换
    func(s);        // 自动类型转换

    return 0;
}
</code></pre>

